// Code execution utilities for preview and testing
import { z } from 'zod'

// Schema pour valider le code d'entr√©e
const CodeExecutionSchema = z.object({
  html: z.string(),
  css: z.string().optional(),
  javascript: z.string().optional(),
  framework: z.enum(['react', 'vue', 'vanilla', 'nextjs', 'svelte']),
  dependencies: z.array(z.string()).optional(),
})

export type CodeExecution = z.infer<typeof CodeExecutionSchema>

// Fonction pour cr√©er un HTML complet avec le code
export function createFullHTML(code: CodeExecution): string {
  const { html, css, javascript, framework } = code

  let fullHTML = `<!DOCTYPE html>
<html lang="fr">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Application G√©n√©r√©e</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <style>
        ${css || ''}
    </style>
</head>
<body>
    ${html}
    ${javascript ? `<script>${javascript}</script>` : ''}
</body>
</html>`

  return fullHTML
}

// Fonction pour cr√©er un package.json pour les frameworks
export function createPackageJson(
  framework: string,
  dependencies: string[] = []
): string {
  const baseDeps = {
    react: {
      'react': '^18.2.0',
      'react-dom': '^18.2.0',
    },
    vue: {
      'vue': '^3.3.0',
    },
    nextjs: {
      'next': '^14.0.0',
      'react': '^18.2.0',
      'react-dom': '^18.2.0',
    },
    svelte: {
      'svelte': '^4.0.0',
    },
    vanilla: {},
  }

  const frameworkDeps = baseDeps[framework as keyof typeof baseDeps] || {}
  const customDeps = dependencies.reduce((acc, dep) => {
    acc[dep] = 'latest'
    return acc
  }, {} as Record<string, string>)

  const packageJson = {
    name: 'generated-app',
    version: '1.0.0',
    description: 'Application g√©n√©r√©e avec Rork AI Builder',
    main: 'index.js',
    scripts: {
      dev: framework === 'nextjs' ? 'next dev' : 'vite',
      build: framework === 'nextjs' ? 'next build' : 'vite build',
      start: framework === 'nextjs' ? 'next start' : 'vite preview',
    },
    dependencies: {
      ...frameworkDeps,
      ...customDeps,
    },
    devDependencies: {
      '@types/react': '^18.2.0',
      '@types/react-dom': '^18.2.0',
      'typescript': '^5.0.0',
      'vite': '^4.4.0',
    },
  }

  return JSON.stringify(packageJson, null, 2)
}

